INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(51, 'Vịnh Hạ Long', 'HL001', '1500000', 1, '1638545370_banner01.jpg', '<p>Khởi h&agrave;nh&nbsp;<strong>18/12/2021</strong></p>\r\n\r\n<p>Thời gian 1<strong>&nbsp;ng&agrave;y</strong></p>\r\n\r\n<p>Nơi khởi h&agrave;nh <strong>Quảng Ninh</strong></p>\r\n\r\n<p>Số lượng nhận :20</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211021013311_594016.jpg\" /></p>\r\n\r\n<p><strong>Giữa cuộc sống x&ocirc; bồ, tấp nập hiện nay, để t&igrave;m được một kh&ocirc;ng gian y&ecirc;n b&igrave;nh, giản dị quả thật kh&ocirc;ng hề đơn giản một ch&uacute;t n&agrave;o. Thế nhưng, ngay giữa vịnh Hạ Long ồn &agrave;o ấy, l&agrave;ng ch&agrave;i Vung Vi&ecirc;ng vẫn giữ được sự đơn sơ, mộc mạc v&agrave; y&ecirc;n b&igrave;nh. H&atilde;y c&ugrave;ng Vietravel chi nh&aacute;nh Quảng Ninh, kh&aacute;m ph&aacute; ngay l&agrave;ng ch&agrave;i th&uacute; vị n&agrave;y qua tour trải nghiệm: 01 NG&Agrave;Y L&Agrave;M NGƯ D&Acirc;N TẠI L&Agrave;NG CH&Agrave;I VUNG VI&Ecirc;NG</strong></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_200915_Lan%20Ha%20-%20Cat%20Ba%20(3).jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_211021_Lan%20Ha%20-%20Cat%20Ba%20(4).jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_211027_Ha%20Long%20%20(4).jpg\" /></p>\r\n', 1, 52),
(52, 'Long An - Khám Phá Con Đường Xuyên Rừng Tràm Tân Lập (Nhóm 4 khách)', 'TM109001', '14000000', 4, '1638976906_h1222.jpg', '<p>12/12/2021-14/12/2021(3 ng&agrave;y 2 đ&ecirc;m)</p>\r\n', '<p><strong>Để c&oacute; một chuyến đi vừa g&oacute;i gọn trong những ng&agrave;y cuối tuần lại vừa thỏa m&atilde;n sự t&ograve; m&ograve; của những du kh&aacute;ch th&iacute;ch ngao du, Long An l&agrave; một gợi &yacute; l&yacute; tưởng. &ldquo;Nếu kh&ocirc;ng c&oacute; thời gian đi xa, h&atilde;y về dạo chơi Long An&rdquo;- c&acirc;u mời gọi th&acirc;n thương của ch&uacute;ng t&ocirc;i c&oacute; khiến bạn &#39;giật m&igrave;nh&#39; bởi Long An đ&atilde; qu&aacute; gần v&agrave; qu&aacute; quen&hellip; Vậy bạn c&oacute; biết con đường t&igrave;nh y&ecirc;u&rdquo; của l&agrave;ng nổi T&acirc;n Lập? Nơi sở hữu một trong những con đường xuy&ecirc;n rừng tr&agrave;m d&agrave;i nhất Việt Nam phủ d&agrave;y lớp l&aacute; c&ugrave;ng r&ecirc;u phong đầy huyền ảo cho bạn thỏa th&iacute;ch thẩn thơ với &ldquo;vạn g&oacute;c sống ảo&rdquo; v&agrave; c&ograve;n hơn thế nữa &hellip;</strong></p>\r\n', 1, 52),
(53, 'Quy Nhơn - Eo Gió - Kỳ Co ', 'QN14325293', '12000000', 4, '1639008120_beach-in-Quy-Nhon_709067188.jpg', '<p>Khởi h&agrave;nh&nbsp;<strong>15/12/2021</strong></p>\r\n\r\n<p>Thời gian&nbsp;<strong>3 ng&agrave;y</strong></p>\r\n\r\n<p>Nơi khởi h&agrave;nh&nbsp;<strong>TP. Hồ Ch&iacute; Minh</strong></p>\r\n\r\n<p>Số lượng nhận <strong>4</strong></p>\r\n', '<h1>&nbsp;</h1>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211105010254_051825.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kh&aacute;m ph&aacute; biển xanh miền duy&ecirc;n hải Quy Nhơn với c&aacute;c điểm nổi bật: Khu du lịch Hầm H&ocirc;: L&acirc;m vi&ecirc;n hoang d&atilde; với d&ograve;ng suối m&aacute;t y&ecirc;n b&igrave;nh v&agrave; n&ecirc;n thơ, đo&agrave;n kh&aacute;m ph&aacute; cảnh non nước hữu t&igrave;nh&hellip; Đảo Kỳ Co - với m&agrave;u nước trong xanh, b&atilde;i c&aacute;t d&agrave;i trắng mịn v&agrave; những cơn s&oacute;ng biển &ecirc;m &aacute;i Eo Gi&oacute;: Nơi vẫn c&ograve;n giữ được cho m&igrave;nh vẻ đẹp tự nhi&ecirc;n đầy hoang sơ. Đứng từ tr&ecirc;n cao nh&igrave;n xuống, Eo Gi&oacute; như một bức tranh sơn thủy hữu t&igrave;nh với trời, m&acirc;y, nước&hellip; biển n&uacute;i &ocirc;m nhau vừa gợi cảm, vừa hoang sơ đầy quyến rũ.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211203114957_433285.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211203115036_026469.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211129093607_602785.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_200914_BIEN%20QUY%20NHON.jpg\" /></p>\r\n', 1, 52),
(54, 'Combo 2N1Đ Vinpearl Resort & Spa Hạ Long dành cho khách nội tỉnh Quảng Ninh', 'QNNN1234q', '1345000', 2, '1639008578_khach-san-samdi-da-nang.jpg', '<p>11-12/1/2022</p>\r\n', '<p>L&agrave; khu nghỉ dưỡng đầu ti&ecirc;n v&agrave; duy nhất tại miền Bắc Việt Nam được x&acirc;y dựng ho&agrave;n to&agrave;n tr&ecirc;n mặt biển,&nbsp;<strong>Vinpearl Resort &amp; Spa Hạ Long</strong>&nbsp;mang d&aacute;ng h&igrave;nh ki&ecirc;u kỳ của một t&ograve;a l&acirc;u d&agrave;i tr&aacute;ng lệ với kiến tr&uacute;c được lấy cảm hứng từ nh&agrave; h&aacute;t Rennes - Ph&aacute;p. Cảnh quan của resort g&acirc;y ấn tượng kh&oacute; qu&ecirc;n với 3 b&atilde;i biển ri&ecirc;ng tư, khu&ocirc;n vi&ecirc;n xanh v&agrave; hồ bơi ngo&agrave;i trời rộng tới 1.200m2. Đặc biệt, hệ thống ph&ograve;ng nghỉ rộng r&atilde;i được thiết kế tinh mỹ với ban c&ocirc;ng lớn, l&agrave; nơi l&yacute; tưởng để trải nghiệm thức giấc tr&ecirc;n vịnh biển kỳ quan c&ugrave;ng th&agrave;nh phố Hạ Long xinh đẹp.</p>\r\n', 1, 51),
(55, '2N1Đ Bình Minh - Kỷ niệm ngọt ngào', 'KS827264', '1200000', 2, '1639008958_muong-thanh.jpg', '<p>2N1Đ B&igrave;nh Minh - Kỷ niệm ngọt ng&agrave;o</p>\r\n', '<p>L&agrave; khu nghỉ dưỡng đầu ti&ecirc;n v&agrave; duy nhất tại miền Bắc Việt Nam được x&acirc;y dựng ho&agrave;n to&agrave;n tr&ecirc;n mặt biển, <strong>B&igrave;nh Minh</strong>&nbsp;mang d&aacute;ng h&igrave;nh ki&ecirc;u kỳ của một t&ograve;a l&acirc;u d&agrave;i tr&aacute;ng lệ với kiến tr&uacute;c được lấy cảm hứng từ nh&agrave; h&aacute;t Rennes - Ph&aacute;p. Cảnh quan của resort g&acirc;y ấn tượng kh&oacute; qu&ecirc;n với 3 b&atilde;i biển ri&ecirc;ng tư, khu&ocirc;n vi&ecirc;n xanh v&agrave; hồ bơi ngo&agrave;i trời rộng tới 1.200m2. Đặc biệt, hệ thống ph&ograve;ng nghỉ rộng r&atilde;i được thiết kế tinh mỹ với ban c&ocirc;ng lớn, l&agrave; nơi l&yacute; tưởng để trải nghiệm thức giấc tr&ecirc;n vịnh biển kỳ quan c&ugrave;ng th&agrave;nh phố Hạ Long xinh đẹp.</p>\r\n', 1, 51),
(56, 'AVANI Quy Nhơn Resort', 'THK1231', '700000', 1, '1639017983_khách sạn.jpg', '<p>2N1Đ</p>\r\n', '<h1>AVANI Quy Nhơn Resort &amp; Spa</h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Địa điểm : B&atilde;i D&agrave;i, Ghềnh R&aacute;ng, Th&agrave;nh phố Quy Nhơn, T&igrave;nh B&igrave;nh Định, Quy Nhơn 880000 Việt Nam</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://dynamic-media-cdn.tripadvisor.com/media/photo-o/1c/cd/6c/51/infinity-water-feature.jpg?w=900&amp;h=-1&amp;s=1\" /><img alt=\"\" src=\"https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0a/69/41/7e/sunrise-at-avani-quy.jpg?w=800&amp;h=-1&amp;s=1\" /><img alt=\"\" src=\"https://dynamic-media-cdn.tripadvisor.com/media/photo-o/07/3c/85/2f/avani-quy-nhon-resort.jpg?w=800&amp;h=-1&amp;s=1\" /></p>\r\n\r\n<p>Nếu bạn đang t&igrave;m kiếm khu nghỉ dưỡng sang trọng ở Quy Nhơn, h&atilde;y thử nghiệm AVANI Quy Nhơn Resort .</p>\r\n\r\n<p>C&aacute;c ph&ograve;ng tại AVANI Quy Nhơn Resort cung cấp tv m&agrave;n h&igrave;nh phẳng, quầy bar mini v&agrave; điều h&ograve;a nhiệt độ, v&agrave; kh&aacute;ch c&oacute; thể được kết nối wifi miễn ph&iacute;.</p>\r\n\r\n<p>Ngo&agrave;i ra, khi nghỉ dưỡng tại AVANI Quy Nhơn Resort kh&aacute;ch c&oacute; thể d&ugrave;ng dịch vụ ph&ograve;ng v&agrave; nh&acirc;n vi&ecirc;n hỗ trợ kh&aacute;ch. Bạn cũng c&oacute; thể tận hưởng bể bơi v&agrave; quầy bar cạnh bể bơi. Cần chỗ đỗ xe ? Đỗ xe miễn ph&iacute; c&oacute; sẵn tại AVANI Quy Nhơn Resort .</p>\r\n\r\n<p>C&aacute;c nh&acirc;n vi&ecirc;n của AVANI Quy Nhơn Resort &amp; Spa mong muốn được phục vụ bạn trong k&igrave; nghỉ sắp tới</p>\r\n\r\n<figure class=\"easyimage easyimage-full\"><img alt=\"\" src=\"blob:http://localhost/63733947-e6b8-4691-8a7e-5169dd1f964a\" width=\"1024\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n\r\n<p><strong>Tiện nghi của kh&aacute;ch sạn</strong></p>\r\n\r\n<ul>\r\n	<li>B&atilde;i đỗ xe miễn ph&iacute;</li>\r\n	<li>Internet tốc độ miễn ph&iacute; (WiFi)</li>\r\n	<li>Bể bơi</li>\r\n	<li>Bãi bi&ecirc;̉n</li>\r\n	<li>Cho thu&ecirc; xe đạp</li>\r\n	<li>C&aacute;c hoạt động cho trẻ em (Ph&ugrave; hợp với trẻ em / gia đ&igrave;nh)</li>\r\n</ul>\r\n\r\n<p><strong>Tiện nghi trong ph&ograve;ng</strong></p>\r\n\r\n<ul>\r\n	<li>M&agrave;n che &aacute;nh s&aacute;ng</li>\r\n	<li>&Aacute;o cho&agrave;ng tắm</li>\r\n	<li>Điều h&ograve;a nhiệt độ</li>\r\n	<li>B&atilde;i biển ri&ecirc;ng</li>\r\n	<li>B&agrave;n l&agrave;m việc</li>\r\n	<li>M&aacute;y pha c&agrave; ph&ecirc; / tr&agrave;</li>\r\n	<li>Truyền h&igrave;nh c&aacute;p / truyền h&igrave;nh vệ tinh</li>\r\n	<li>V&ograve;i sen đứng</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Loại ph&ograve;ng</strong></p>\r\n\r\n<ul>\r\n	<li>Ngắm cảnh n&uacute;i</li>\r\n	<li>Ngắm cảnh biển</li>\r\n	<li>Phòng kh&ocirc;ng hút thu&ocirc;́c</li>\r\n	<li>Ph&ograve;ng Suite</li>\r\n	<li>Ph&ograve;ng cho gia đ&igrave;nh</li>\r\n</ul>\r\n', 1, 51),
(57, 'Muong Thanh Quy Nhon Hotel', 'TTN524162', '8000000', 8, '1639018716_muong-thanh-quy-nhon.jpg', '<p>8-10/1/2022(3N2Đ)</p>\r\n', '<p>H&atilde;y tự hỏi tại sao nhiều kh&aacute;ch du lịch đ&atilde; lựa chọn Muong Thanh Quy Nhon Hotel khi đến Quy Nhơn. Kh&aacute;ch sạn l&agrave; sự tổng hợp của những gi&aacute; trị l&yacute; tưởng, sự thoải m&aacute;i v&agrave; thuận tiện như khung cảnh d&agrave;nh cho gia đ&igrave;nh c&ugrave;ng với loạt tiện nghi được thiết kế cho những kh&aacute;ch du lịch như bạn.</p>\r\n\r\n<p>Bạn sẽ tận hưởng căn ph&ograve;ng với quầy bar mini, tủ lạnh v&agrave; điều h&ograve;a nhiệt độ, v&agrave; bạn chắc chắn sẽ được kết nối internet trong thời gian n&agrave;y bởi Muong Thanh Quy Nhon Hotel cung cấp cho du kh&aacute;ch wifi miễn ph&iacute;.</p>\r\n\r\n<p>Khách sạn c&ograve;n cung cấp nh&acirc;n vi&ecirc;n hỗ trợ kh&aacute;ch v&agrave; dịch vụ ph&ograve;ng. Hơn nữa, Muong Thanh Quy Nhon Hotel với bể bơi v&agrave; bữa s&aacute;ng, mang lại những gi&acirc;y ph&uacute;t thư gi&atilde;n trốn khỏi một ng&agrave;y bận rộn. Với kh&aacute;ch c&oacute; xe cộ đỗ xe miễn ph&iacute; c&oacute; sẵn.</p>\r\n\r\n<p>Quy Nhơn c&oacute; rất nhiều nh&agrave; h&agrave;ng kiểu &aacute;. V&igrave; thế khi ở đ&acirc;y, h&atilde;y nhớ thử những điểm ẩm thực y&ecirc;u th&iacute;ch như Cine, Bun thit nuong v&agrave; Nha Hang 114 Xuan Dieu, phục vụ một số m&oacute;n ăn rất tuyệt vời.</p>\r\n\r\n<p>Tr&ecirc;n hết, Muong Thanh Quy Nhon Hotel l&agrave;m đơn giản ho&aacute; việc trải nghiệm những điểm đến tuyệt vời ở Quy Nhơn như Th&aacute;p Đ&ocirc;i (1,8 km) v&agrave; Long Khanh Pagoda (0,9 km), v&agrave; chỉ nằm ở khoảng c&aacute;ch rất gần.</p>\r\n\r\n<p>Tại Muong Thanh Quy Nhon Hotel, sự thoải m&aacute;i v&agrave; h&agrave;i l&ograve;ng của bạn được đặt l&ecirc;n h&agrave;ng đầu, v&agrave; họ mong muốn được ch&agrave;o đ&oacute;n bạn tới Quy Nhơn.</p>\r\n\r\n<p><strong>Tiện nghi của kh&aacute;ch sạn</strong></p>\r\n\r\n<ul>\r\n	<li><em>B&atilde;i đỗ xe miễn ph&iacute;</em></li>\r\n	<li><em>Internet tốc độ miễn ph&iacute; (WiFi)</em></li>\r\n	<li><em>Bể bơi</em></li>\r\n	<li><em>Bữa sáng mi&ecirc;̃n phí</em></li>\r\n	<li><em>Bãi bi&ecirc;̉n</em></li>\r\n	<li><em>S&acirc;n quần vợt</em></li>\r\n	<li><em>Xe đưa đ&oacute;n s&acirc;n bay</em></li>\r\n	<li><em>Tiện nghi cho hội nghị</em></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tiện nghi trong ph&ograve;ng</strong></p>\r\n\r\n<ul>\r\n	<li><em>Điều h&ograve;a nhiệt độ</em></li>\r\n	<li><em>Dịch vụ ph&ograve;ng</em></li>\r\n	<li><em>K&eacute;t sắt</em></li>\r\n	<li><em>Quầy bar mini</em></li>\r\n	<li><em>Tủ lạnh</em></li>\r\n	<li><em>TV m&agrave;n h&igrave;nh phẳng</em></li>\r\n</ul>\r\n\r\n<p><strong>Loại ph&ograve;ng</strong></p>\r\n\r\n<ul>\r\n	<li><em>Ngắm cảnh biển</em></li>\r\n	<li><em>Phòng kh&ocirc;ng hút thu&ocirc;́c</em></li>\r\n	<li><em>Ph&ograve;ng Suite</em></li>\r\n	<li><em>C&oacute; ph&ograve;ng h&uacute;t thuốc</em></li>\r\n</ul>\r\n', 1, 51),
(58, 'Nhà hàng El Gaucho:  “Nhà Hàng Beefsteak 5 Sao”', 'NH565656', '300000', 1, '1639019317_shalimar.jpg', '<figure class=\"easyimage easyimage-side\"><img alt=\"\" src=\"blob:http://localhost/efa751d8-8d09-4c73-a181-9fda21040af9\" width=\"1080\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Nằm ngay tr&ecirc;n con đường Hai B&agrave; Trưng, Quận 1, TP.HCM&nbsp; nh&agrave; h&agrave;ng El Gaucho l&agrave; nh&agrave; h&agrave;ng đầu ti&ecirc;n tại Việt Nam của chuỗi nh&agrave; h&agrave;ng beefsteak. Ngay từ kh&ocirc;ng gian qu&aacute;n đ&atilde; to&aacute;t l&ecirc;n vẻ sang chảnh, hiện đại cho đến hương vị hấp dẫn, độc đ&aacute;o của c&aacute;c c&aacute;c m&oacute;n beefsteak.</p>\r\n\r\n<p>Nhiều thực kh&aacute;ch nhận định rằng, đi khắp&nbsp;<strong>c&aacute;c nh&agrave; h&agrave;ng S&agrave;i G&ograve;n</strong>&nbsp;cũng kh&ocirc;ng nơi đ&acirc;y c&oacute; được hương vị thơm ngon v&agrave; hấp dẫn như m&oacute;n beefsteak tại đ&acirc;y, ch&uacute;ng mềm v&agrave; mọng nước như tan ra trong miệng.</p>\r\n\r\n<p>Du kh&aacute;ch c&oacute; thể gh&eacute; địa chỉ: 74 Hai B&agrave; Trưng, P. Bến Ngh&eacute;, Quận 1, TP.HCM.</p>\r\n', 1, 50),
(59, 'Nhà hàng Red House_Hà Nội', 'NH7367363', '5000000', 8, '1639019795_we-only-serve-fresh-icelandic.jpg', '<p>Nh&agrave; h&agrave;ng Red House</p>\r\n', '<p>Được th&agrave;nh lập từ năm 2008, Red House l&agrave; một trong những&nbsp;<strong>nh&agrave; h&agrave;ng H&agrave; Nội foody&nbsp;</strong>được b&igrave;nh chọn l&agrave; một trong những qu&aacute;n ăn mang đậm phong c&aacute;ch Singapore ngay tr&ecirc;n đất Việt.</p>\r\n\r\n<p>Từ những ch&aacute;o ếch, cua sốt ớt cho đến cua sốt ti&ecirc;u đen hay những m&oacute;n cơm ni&ecirc;u,&hellip;đều mang những sự trải nghiệm ẩm thực độc đ&aacute;o v&agrave; v&ocirc; c&ugrave;ng th&uacute; vị.</p>\r\n\r\n<p>Du kh&aacute;ch c&oacute; thể gh&eacute;&nbsp;<strong>nh&agrave; h&agrave;ng</strong>&nbsp;tại địa chỉ: 56 An dương Vương,H&agrave; Nội&nbsp; để thưởng thức hương vị Singapore truyền thống.</p>\r\n\r\n<p><img alt=\"10 Nhà hàng Sài Gòn TPHCM ngon và nổi tiếng đáng để thưởng thức\" src=\"https://3.bp.blogspot.com/-5Ms6uMPBsnk/W3Xoro_xvoI/AAAAAAAAXR0/pPAkFuu58H8o02chQQEFS3V6elrqmTBvwCLcBGAs/s1600/nha-hang-sai-gon-tphcm-6.jpg\" width=\"1200\" /></p>\r\n', 1, 50),
(60, 'Namo – Artisanal Pizzerian Quy Nhơn', 'NH783738', '1000000', 2, '1639021487_nha-hang-sai-gon-tphcm-2.jpg', '', '<p>&nbsp;</p>\r\n\r\n<p><strong>M&Oacute;N ĂN</strong></p>\r\n\r\n<p>Ki&ecirc;̉u Á, Đ&ocirc;̀ nướng, Ki&ecirc;̉u Hàn Qu&ocirc;́c</p>\r\n\r\n<p><strong>CHẾ ĐỘ ĂN ĐẶC BIỆT</strong></p>\r\n\r\n<p>T&ugrave;y chọn ăn chay, T&ugrave;y chọn đồ ăn kh&ocirc;ng c&oacute; gluten</p>\r\n\r\n<figure class=\"easyimage easyimage-full\"><img alt=\"\" src=\"blob:http://localhost/d3fbc08e-3b59-4c6f-98eb-11e6c7802518\" width=\"800\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n\r\n<p><strong>BỮA ĂN</strong></p>\r\n\r\n<p>Bữa t&ocirc;́i,Buổi trưa</p>\r\n\r\n<p><strong>ĐẶC TRƯNG</strong></p>\r\n\r\n<p>Đặt ch&ocirc;̃, Ng&ocirc;̀i ngoài trời, Ăn tối ph&ograve;ng ri&ecirc;ng, Chỗ ngồi, C&oacute; b&atilde;i đỗ xe, B&atilde;i đỗ xe c&oacute; v&eacute;, Đ&ocirc;̃ xe có người coi, Tivi, C&oacute; ghế ăn cho b&eacute;, Lối v&agrave;o ra cho xe lăn, Phục vụ đồ uống c&oacute; cồn, Qu&acirc;̀y bar đ&acirc;̀y đủ, Chấp nhận thẻ American Express, Chấp nhận thẻ Mastercard, Chấp nhận thẻ Visa, Wifi miễn ph&iacute;, Chấp nhận thẻ t&iacute;n dụng, Dịch vụ b&agrave;n</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 50),
(61, 'Trois Gourmands Sài Gòn', 'NH8782928', '300000', 1, '1639021406_nha-hang-vintage-1885-406813.jpg', '', '<ul>\r\n	<li>Địa chỉ: 4/6 Hai B&agrave; Trưng, Bến Ngh&eacute;, Quận 1, TPHCM</li>\r\n	<li>Số điện thoại: 028 3822 7988</li>\r\n	<li>Gi&aacute; cả: 100.000đ &ndash; 400.000đ</li>\r\n	<li>Giờ mở cửa: 11:00 &ndash; 22:00</li>\r\n</ul>\r\n\r\n<p><img alt=\"Top 26 Nhà hàng Sài Gòn TPHCM ngon và nổi tiếng đáng để thưởng thức\" sizes=\"(max-width: 1024px) 100vw, 1024px\" src=\"https://i0.wp.com/kenhhomestay.com/wp-content/uploads/2020/05/nha-hang-sai-gon-15.jpg\" srcset=\"//i1.wp.com/kenhhomestay.com/wp-content/uploads/2020/05/nha-hang-sai-gon-15.jpg 1024w, //i1.wp.com/kenhhomestay.com/wp-content/uploads/2020/05/nha-hang-sai-gon-15-768x512.jpg 768w\" width=\"1024\" />Nh&agrave; h&agrave;ng &Yacute; nức tiếng nhất đất S&agrave;i Th&agrave;nh, được nghệ sĩ check-in &ldquo;tấp nập&rdquo; nhất ch&iacute;nh l&agrave; Namo &ndash; Artisanal Pizzeria. Namo nổi bật từ thiết kế hiện đại b&ecirc;n ngo&agrave;i đến vẻ lộng l&atilde;y b&ecirc;n trong nội thất, view đẹp.</p>\r\n\r\n<p>Thực kh&aacute;ch c&oacute; thể ngồi b&agrave;n hoặc ngồi quầy bar để vừa thưởng thức m&oacute;n ăn vừa tận mắt nh&igrave;n thấy qu&aacute; tr&igrave;nh nướng pizza t&agrave;i t&igrave;nh của c&aacute;c đầu bếp đẳng cấp của nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh pizza, c&aacute;c m&oacute;n hải sản, m&igrave; &Yacute;, cocktail đều rất hấp dẫn v&agrave; đ&aacute;ng cho bạn thử &iacute;t nhất 1 lần trong đời đấy.</p>\r\n\r\n<p>Chuỗi nh&agrave; h&agrave;ng S&agrave;i G&ograve;n tuyệt vời ở tr&ecirc;n, c&oacute; địa điểm n&agrave;o l&agrave;m bạn đặc biệt t&ograve; m&ograve;, th&iacute;ch th&uacute; kh&ocirc;ng? Kh&ocirc;ng cần chờ đợi, đặt lịch hẹn hoặc đến thẳng nh&agrave; h&agrave;ng để trải nghiệm ngay n&agrave;o!</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; top 26&nbsp;<strong>nh&agrave; h&agrave;ng S&agrave;i G&ograve;n</strong>&nbsp;<strong>nổi tiếng</strong>&nbsp;m&agrave; du kh&aacute;ch n&ecirc;n thử khi đến S&agrave;i G&ograve;n. Hy vọng những th&ocirc;ng tin tr&ecirc;n sẽ c&oacute; thể gi&uacute;p &iacute;ch được đối với những người d&ugrave;ng.</p>\r\n', 1, 50),
(62, 'Săn mây phố núi Đà Lạt', 'DL7382729', '500000', 1, '1639022666_cafe-tui-mo-to-12-768x512.jpg', '<p>Khởi h&agrave;nh&nbsp;<strong>12/12/2021</strong></p>\r\n\r\n<p>Thời gian&nbsp;<strong>1 ng&agrave;y</strong></p>\r\n\r\n<p>Nơi khởi h&agrave;nh&nbsp;<strong>Đ&agrave; Lạt</strong></p>\r\n\r\n<p>Số chỗ c&ograve;n nhận <strong>10</strong></p>\r\n', '<p><strong>Săn m&acirc;y &ndash; Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng khoảnh khắc chuyển m&igrave;nh của đất trời Đ&agrave; Lạt khi những tia nắng b&igrave;nh minh đầu ti&ecirc;n xuất hiện giữa những tầng m&acirc;y m&ecirc;nh m&ocirc;ng đổ d&agrave;i hư ảo, tinh kh&ocirc;i v&agrave; quyến rũ đến lạ thường.</strong></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_211021032057_941630.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_211021_Da%20Lat%20%20(3).jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_200914_Da%20Lat%20%20(6).jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_211021_Vuon%20dau.jpg\" /></p>\r\n', 1, 52),
(63, 'Nha Trang -Thành Phố Biển', 'DL9282028', '2000000', 1, '1639023192_Anam-Resort-Nha-Trang-vietnam-25.jpg', '<p>Khởi h&agrave;nh&nbsp;<strong>20/01/2022</strong></p>\r\n\r\n<p>Thời gian&nbsp;<strong>3 ng&agrave;y</strong></p>\r\n\r\n<p>Nơi khởi h&agrave;nh&nbsp;<strong>TP. Hồ Ch&iacute; Minh</strong></p>\r\n\r\n<p>Số chỗ c&ograve;n nhận <strong>10</strong></p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_201007102810_270947.jpg\" /></p>\r\n\r\n<p><strong>Nha Trang với kh&iacute; hậu &ocirc;n h&ograve;a, biển xanh trong quanh năm c&ugrave;ng những điểm vui chơi bậc nhất v&agrave; kh&ocirc;ng ngừng đổi mới hằng ng&agrave;y lu&ocirc;n thu h&uacute;t du kh&aacute;ch gần xa. Đến với Nha Trang, du kh&aacute;ch kh&ocirc;ng chỉ tận hưởng những đợt gi&oacute; biển trong nắng ấm m&agrave; c&ograve;n c&oacute; dịp thưởng thức hải sản tươi ngon c&ugrave;ng sự ch&agrave;o đ&oacute;n nồng hậu từ những người d&acirc;n v&ugrave;ng biển nghĩa t&igrave;nh, cho Qu&yacute; kh&aacute;ch trải nghiệm kh&oacute; qu&ecirc;n tại v&ugrave;ng đất n&agrave;y.</strong></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_201007102912_986218.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/tour/tfd_201007103032_140108.jpg\" /></p>\r\n\r\n<p><img alt=\"image\" src=\"https://media.travel.com.vn/destination/dc_200914_NHA%20TRANG_768444763.jpg\" /></p>\r\n', 1, 52);

